import React from "react";
import { inject, observer } from "mobx-react";

import Navigation from "../../components/Navigation";
import Clearfix from "../../components/Clearfix";
import Container from "../../components/Container";
import SlideList from "../../components/SlideList";
import ListItem01 from "../../components/ListItem01";
import CarouselList from "../../components/CarouselList";
import ListItem02 from "../../components/ListItem02";

import MainIndexVisualCarousel from "../../components/page-dependency/Main-index-visual-carousel";
import MainIndexQuickLinkContainer from "../../components/page-dependency/Main-index-quick-link-container";
import ReserveButton01 from "../../components/button/ReserveButton01";
import SlideList01 from "../../components/list/SlideList01";
import ImgListItem01 from "../../components/item/ImgListItem01";
import ImgListItem02 from "../../components/item/ImgListItem02";
import ImgListItem03 from "../../components/item/ImgListItem03";
import FishListItem01 from "../../components/item/FishListItem01";
import MainIndexAd from "../../components/page-dependency/Main-index-ad";
import MainIndexMap from "../../components/page-dependency/Main-index-map";
import Space from "../../components/layout/Space";

export default inject("PageStore")(
  observer(
    class IndexPage extends React.Component {
      async componentDidMount() {
        const { PageStore } = this.props;
        await PageStore.injectScript("/assets/js/jquery.touchSwipe.min.js");
        await PageStore.injectScript("/assets/js/swiper.min.js");
        await PageStore.injectScript("/assets/js/default.js");
      }
      componentWillUnmount() {
        const { PageStore } = this.props;
        PageStore.removeInjectetdScripts();
      }
      /** 렌더링 */
      render() {
        return (
          <>
            {/** 네비게이션 */}
            <Navigation type={"index"} />

            {/** Visual Carousel */}
            <CarouselList
              id={"carousel-visual"}
              renderItem={(data) => (
                <img src={data.imgSrc} className="d-block w-100" alt="" />
              )}
              list={[
                { imgSrc: "/assets/img/slide1.jpg" },
                { imgSrc: "/assets/img/slide1.jpg" },
                { imgSrc: "/assets/img/slide1.jpg" },
              ]}
            />

            <Clearfix />

            {/** Quick Link */}
            <Container>
              <nav className="nav nav-pills quick nav-justified">
                <a className="nav-link" href="search-reserv.html">
                  <img src="/assets/img/svg/quick-boat.svg" alt="바다낚시" />{" "}
                  선상 실시간 예약
                </a>{" "}
                &nbsp;&nbsp;
                <a className="nav-link" href="search-reserv.html">
                  <img src="/assets/img/svg/quick-rocks.svg" alt="갯바위낚시" />{" "}
                  갯바위 실시간 예약
                </a>
              </nav>
            </Container>
            <Clearfix />

            {/** Content */}
            <Container cls={"nopadding"}>
              {/**** Content > 실시간 조황 */}
              <SlideList
                title={"실시간 조황"}
                renderItem={(data) => <ListItem01 {...data} />}
                list={[
                  {
                    navigateTo: "boat-detail.html",
                    imgSrc: "/assets/img/sample/live1.jpg",
                    isLive: true,
                    title: "챔피온 1호",
                    textPrimary: "돌문어",
                    location: "전남 여수시",
                  },
                  {
                    navigateTo: "boat-detail.html",
                    imgSrc: "/assets/img/sample/live1.jpg",
                    playTime: "20:17",
                    title: "챔피온 1호",
                    textPrimary: "돌문어",
                    location: "전남 여수시",
                  },
                ]}
                hasMore={true}
                navigateMore={"boat.html"}
              ></SlideList>
              {/**** Content > 출조 정보 */}
              <SlideList
                title={"출조 정보"}
                renderItem={(data) => <ListItem01 {...data} />}
                list={[
                  {
                    navigateTo: "boat-detail.html",
                    imgSrc: "/assets/img/sample/boat1.jpg",
                    playTime: "20:17",
                    title: "챔피온 1호",
                    textPrimary: "돌문어",
                    location: "전남 여수시",
                    price: 70000,
                  },
                  {
                    navigateTo: "boat-detail.html",
                    imgSrc: "/assets/img/sample/boat2.jpg",
                    playTime: "20:17",
                    title: "챔피온 1호",
                    textPrimary: "돌문어",
                    location: "전남 여수시",
                    price: 70000,
                  },
                ]}
                hasMore={true}
                navigateMore={"boat.html"}
              ></SlideList>

              {/**** Content > 광고배너 */}
              <div className="mainAdWrap">
                <h5>
                  추천 업체 &nbsp;
                  <small className="text-primary">
                    <strong>AD</strong>
                  </small>
                </h5>
                <CarouselList
                  id={"carouselRecommend"}
                  renderItem={(data) => <ListItem02 {...data} />}
                  list={[
                    {
                      navigateTo: "boat-detail.html",
                      imgSrc: "/assets/img/sample/boat2.jpg",
                      title: "어복황제1호",
                      textPrimary: "쭈꾸미",
                      location: "전남 진도군 27km",
                      price: 70000,
                    },
                    {
                      navigateTo: "boat-detail.html",
                      imgSrc: "/assets/img/sample/boat2.jpg",
                      title: "어복황제1호",
                      textPrimary: "쭈꾸미",
                      location: "전남 진도군 27km",
                      price: 70000,
                    },
                  ]}
                />
              </div>

              {/**** Content > 조황 일지 */}
              <SlideList
                title={"조황 일지"}
                renderItem={(data) => <ListItem01 {...data} />}
                list={[
                  {
                    navigateTo: "boat-detail.html",
                    imgSrc: "/assets/img/sample/photo8.jpg",
                  },
                ]}
              />
            </Container>
          </>
        );
      }
    }
  )
);
