/* eslint-disable jsx-a11y/anchor-is-valid */
import React from "react";

const MainTabs = ({ pathname, onClick, location }) => {
  console.log(location);
  // --> 탭 액티브 여부
  let activeIndex = 0;
  if (pathname.indexOf("/main/index") !== -1) activeIndex = 0;
  else if (pathname.indexOf("/main/boat") !== -1) activeIndex = 1;
  else if (pathname.indexOf("/main/rock") !== -1) activeIndex = 2;
  else if (pathname.indexOf("/main/story") !== -1) activeIndex = 3;
  else if (pathname.indexOf("/main/my") !== -1) activeIndex = 4;
  // -- > 탭 메뉴 리스트
  const menus = [
    { pathname: `/main/index`, iconName: "tab_home", text: "홈" },
    { pathname: `/main/boat`, iconName: "tab_boat", text: "선상" },
    { pathname: `/main/rock`, iconName: "tab_rock", text: "갯바위" },
    { pathname: `/main/story`, iconName: "tab_story", text: "어복스토리" },
    { pathname: `/main/my`, iconName: "tab_my", text: "마이메뉴" },
  ];
  // --> 렌더링
  return (
    <div className="tab_barwrap fixed-bottom">
      <div className="container nopadding">
        <nav className="nav nav-pills nav-tab nav-justified">
          {menus.map((data, index) => (
            <a
              key={index}
              className={"nav-link"}
              onClick={() => onClick(data.pathname)}
            >
              <figure className={data.iconName}></figure>
              {data.text}
            </a>
          ))}
        </nav>
      </div>
    </div>
  );
};

export default MainTabs;
