/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect } from "react";

const ConfirmModal = ({
  id,
  title,
  innerHtml,
  textOk,
  textCancel,
  onOk,
  onCancel,
}) => {
  id = id || "";
  textCancel = textCancel || "닫기";
  useEffect(() => {
    console.log(id);
  });
  return (
    <div
      className="modal fade"
      id={id}
      tabIndex="-1"
      aria-labelledby={id.concat("Label")}
      aria-hidden="true"
    >
      <div className="modal-dialog modal-sm modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title text-center" id="callModalLabel">
              {title}
            </h5>
          </div>
          <div className="modal-body text-center">{innerHtml}</div>
          <div className="modal-footer-btm">
            <div className="row no-gutters">
              <div className="col-6">
                <a
                  onClick={() => onOk()}
                  className="btn btn-primary btn-lg btn-block"
                  data-dismiss="modal"
                >
                  {textOk}
                </a>
              </div>
              <div className="col-6">
                <a
                  onClick={() => onCancel()}
                  className="btn btn-third btn-lg btn-block"
                  data-dismiss="modal"
                >
                  {textCancel}
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ConfirmModal;
