import React, { useEffect } from "react";

import MainIndexNav from "../../components/page-dependency/Main-index-nav";
import MainIndexVisualCarousel from "../../components/page-dependency/Main-index-visual-carousel";
import MainIndexQuickLinkContainer from "../../components/page-dependency/Main-index-quick-link-container";
import ReserveButton01 from "../../components/button/ReserveButton01";
import SlideList01 from "../../components/list/SlideList01";
import ImgListItem01 from "../../components/item/ImgListItem01";
import ImgListItem02 from "../../components/item/ImgListItem02";
import ImgListItem03 from "../../components/item/ImgListItem03";
import FishListItem01 from "../../components/item/FishListItem01";
import MainIndexAd from "../../components/page-dependency/Main-index-ad";
import MainIndexMap from "../../components/page-dependency/Main-index-map";
import Clearfix from "../../components/layout/Clearfix";
import Space from "../../components/layout/Space";
import { inject, observer } from "mobx-react";

export default inject("PageStore")(
  observer(({ PageStore }) => {
    useEffect(() => {
      (async () => {
        await PageStore.injectScript("/assets/js/jquery.touchSwipe.min.js");
        await PageStore.injectScript("/assets/js/default.js");
        await PageStore.injectScript("/assets/js/swiper.min.js");
      })();
    });
    return (
      <>
        {/** Navigation */}
        <MainIndexNav onSearchClick={() => console.log("link::search.html")} />
        {/** Visual Carousel */}
        <MainIndexVisualCarousel />
        <Clearfix />
        {/** Quick Link */}
        <MainIndexQuickLinkContainer>
          <ReserveButton01
            imgSrc={"/assets/img/svg/quick-boat.svg"}
            text={"선상 실시간 예약"}
            onClick={() => console.log("link::search-reserv.html")}
          />{" "}
          &nbsp;&nbsp;
          <ReserveButton01
            imgSrc={"/assets/img/svg/quick-rocks.svg"}
            text={"갯바위 실시간 예약"}
            onClick={() => console.log("link::search-reserv.html")}
          />
        </MainIndexQuickLinkContainer>
        <Clearfix />
        {/** Contents */}
        <div className="container nopadding">
          {/** 실시간 조황 */}
          <SlideList01
            title={"실시간 조황"}
            hasMore={true}
            onMoreClick={() => console.log("link::실시간조황더보기")}
          >
            <ImgListItem01
              onClick={() => console.log("link::boat-detail.html")}
              imgSrc={"/assets/img/sample/live1.jpg"}
              isLive={true}
              title={"챔피온 1호"}
              textPrimary={"돌문어"}
              location={"전남 여수시"}
            />
          </SlideList01>
          {/** 출조 정보 */}
          <SlideList01
            title={"출조 정보"}
            hasMore={true}
            onMoreClick={() => console.log("link::출조정보더보기")}
          >
            <ImgListItem02
              onClick={() => console.log("link::boat-detail.html")}
              imgSrc={"/assets/img/sample/boat1.jpg"}
              playTime={"20:17"}
              title={"챔피온 1호"}
              textPrimary={"돌문어"}
              location={"전남 여수시"}
              price={70000}
            />
          </SlideList01>
          {/** 광고배너 */}
          <MainIndexAd
            list={[
              {
                imgSrc: "/assets/img/sample/boat2.jpg",
                title: "어복황제1호",
                titlePrimary: "쭈꾸미",
                location: "전남 진도군 27km",
                price: 70000,
              },
            ]}
            onClick={(item) => console.log("광고클릭:" + JSON.stringify(item))}
          />
          {/** 조황일지 */}
          <SlideList01
            title={"조황 일지"}
            hasMore={true}
            onMoreClick={() => console.log("조황일지 더보기")}
          >
            <ImgListItem03
              onClick={() => console.log("조황일지::아이템 클릭")}
              imgSrc={"/assets/img/sample/photo8.jpg"}
              title={"이글스호 백조기 조황"}
              textPrimary={"돌문어"}
              location={"전남 여수시"}
            />
          </SlideList01>
          {/** 지역별 조황 */}
          <MainIndexMap />
          {/** 어종별 조황 */}
          <SlideList01
            title={"어종별 조황"}
            additionalListClass={"fishList"}
            hasMore={true}
            onMoreClick={() => console.log("어종별조황 더보기")}
          >
            <FishListItem01
              onClick={() => console.log("link::boat.html")}
              imgSrc={"/assets/img/fish/fish_icon_01w.svg"}
              name={"갈치"}
              count={17}
            />
          </SlideList01>
        </div>
        <Space />
      </>
    );
  })
);
